const test = require('blue-tape')
const {isCalculation} = require('../formats/isCalculation')

test('allowed variable', async (t) => {
  t.true(isCalculation('max_physical_cpu_cores'))
})

test('valid calculation', async (t) => {
  t.true(isCalculation('max_physical_cpu_cores / 2'))
})

test('invalid expression', async (t) => {
  t.false(isCalculation('console.log(123)'))
})

test('plain number is not a calculation', async (t) => {
  t.false(isCalculation(4))
})
